routerMainnet = web3.to_checksum_address('0x10ED43C718714eb63d5aA57B78B54704E256024E')
factoryMainnet = web3.to_checksum_address('0xcA143Ce32Fe78f1f7019d7d551a6402fC5350c73')

wbnbMainnet = web3.to_checksum_address('0xbb4cdb9cbd36b01bd1cbaebf2de08d9173bc095c')

factoryContract = web3.eth.contract(factoryMainnet,abi=pancake_factory_abi)
routerContract = web3.eth.contract(routerMainnet,abi=pancake_abi)

getLpAddress = factoryContract.functions.getPair(contract_address,wbnbMainnet).call()
print("Liquidity Pool Address: ",getLpAddress)
def get_token_from_lp(getLpAddress):
    router = web3.eth.contract(address=getLpAddress, abi=lpABI)

    tokenA_address = router.functions.token0().call()
    tokenB_address = router.functions.token1().call()
    if tokenA_address != wbnbMainnet:
        return 1
    else:
        return 0

index_reserves= get_token_from_lp(getLpAddress)
print("Token WBNB Reserve Index",index_reserves)

def getReserves(getLpAddress):
    router = web3.eth.contract(address=getLpAddress, abi=lpABI)
    pairReserves = router.functions.getReserves().call()
    pairReserves=  float(web3.from_wei(pairReserves[index_reserves], "ether"))
    return round(pairReserves,2)


print(f"{getReserves(getLpAddress)} BNB")